export const toc = [{
  value: 'Materials',
  id: 'materials',
  children: []
}, {
  value: 'Procedure',
  id: 'procedure',
  children: [{
    value: '1. 3D Printing',
    id: '1-3d-printing',
    children: []
  }, {
    value: '2. Dismantle Genius DX 120 Mouse',
    id: '2-dismantle-genius-dx-120-mouse',
    children: []
  }, {
    value: '3. Assemble the push-button',
    id: '3-assemble-the-push-button',
    children: []
  }, {
    value: '4. Assemble the scroll wheel',
    id: '4-assemble-the-scroll-wheel',
    children: []
  }, {
    value: '5. Assemble the scroll',
    id: '5-assemble-the-scroll',
    children: []
